package xyz.junerver.kotlin

import kotlin.contracts.ExperimentalContracts
import kotlin.contracts.contract

/**
 * 仅数字
 */
val String.isDigitOnly: Boolean
    get() = matches(Regex("^\\d*\$"))

/**
 * 仅字母
 */
val String.isAlphabeticOnly: Boolean
    get() = matches(Regex("^[a-zA-Z]*\$"))

/**
 * 仅数字+字母
 */
val String.isAlphanumericOnly: Boolean
    get() = matches(Regex("^[a-zA-Z\\d]*\$"))

/**
 * Description: 校验是否为合法文件名
 * @author Junerver
 * @date: 2021/12/30-14:31
 * @Email: junerver@gmail.com
 * @Version: v1.0
 * @param
 * @return
 */
fun String.isValidFileName(): Boolean {
    val regex =
        "[^\\s\\\\/:\\*\\?\\\"<>\\|](\\x20|[^\\s\\\\/:\\*\\?\\\"<>\\|])*[^\\s\\\\/:\\*\\?\\\"<>\\|\\.]$"
    return Regex(regex).matches(this)
}

/**
 * 验证是否手机
 */
fun String.isMobile(): Boolean {
    val regex = "(\\+\\d+)?1[34578]\\d{9}$"
    return Regex(regex).matches(this)
}

/**
 * 验证是否电话
 */
fun String.isPhone(): Boolean {
    val regex = "(\\+\\d+)?(\\d{3,4}\\-?)?\\d{7,8}$"
    return Regex(regex).matches(this)
}

