/**
 * 字符串安全转换到整型，转换失败返回0
 */
fun String.safeConvertToInt(): Int = try {
    toInt()
} catch (e: Exception) {
    0
}

/**
 * 字符串安全转换到长整型，转换失败返回0
 */
fun String.safeConvertToLong(): Long = try {
    toLong()
} catch (e: Exception) {
    0L
}

/**
 * 字符串安全转换到双精度类型，转换失败返回0
 */
fun String.safeConvertToDouble(): Double = try {
    toDouble()
} catch (e: Exception) {
    0.0
}

/**
 * 字符串安全转换到短整型类型，转换失败返回0
 */
fun String.safeConvertToShort(): Short = try {
    toShort()
} catch (e: Exception) {
    0
}

/**
 * @author 尧
 * String左对齐，废弃的函数，应该使用标准库实现
 * @see [String.padStart]
 */
@Deprecated("should use standard lib's 'padStart(len,ch)' to instead", ReplaceWith("this.padStart(len, ch)"))
fun String.padLeft(len: Int, ch: Char): String = this.padStart(len, ch)

/**
 * @author 尧
 * String右对齐，废弃的函数，应该使用标准库实现
 * @see [String.padEnd]
 */
@Deprecated("should use standard lib's 'padEnd(len,ch)' to instead", ReplaceWith("this.padEnd(len, ch)"))
fun String.padRight(len: Int, ch: Char): String = this.padEnd(len, ch)

@ExperimentalContracts
fun String?.isNotNullOrEmpty(): Boolean {
    contract {
        returns(true) implies (this@isNotNullOrEmpty != null)
    }
    return this != null && !this.trim().equals("null", true) && this.trim().isNotEmpty()
}
